    render() {
        return (
            <div className="items">

                {this.state.images.map(gal =>  <div className="item" key={gal.id} > <img src={gal.largeImageURL} /> </div> )}

            </div>
        )
    }
}

Gallery.propTypes={
    images:PropTypes.array.isRequired
}

export default Gallery
